import React from 'react';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Fab from '@material-ui/core/Fab';
import NavigationIcon from '@material-ui/icons/Navigation';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { unstable_Box as Box } from '@material-ui/core/Box';

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
];

export default function Recurso(props) {
    const [open, setOpen] = React.useState(false);
    const [value, setValue] = React.useState(false)
    const [values, setValues] = React.useState({
      dataemprestimo: '2019-06-30T10:30',
      datadevolucao: '2019-07-05T10:30',
    });
  function handleClickOpen() {
    setOpen(true);
  }

  function handleCloseTo() {
    alert(`Você agora faz parte da Fila de Alocação do Recurso ${values.dataemprestimo}`)
    setTimeout(function(){ setOpen(false); }, 500);
  }

  const handleChange = name => event => {
    setValues({ ...values, [name]: event.target.value });
  };
  function handleClose() {
    setOpen(false);
  }
  function handleClick() {
    setValue(!value)
  }

        return (<Card style={{maxWidth:320,display:'inline-block',margin:7}}>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">{props.title}</DialogTitle>
        <DialogContent>
          <DialogContentText>
            {props.descricao}
          </DialogContentText>
          <DialogContentText>
          <Typography component="div">
      <Box fontWeight={500} textAlign='left' fontSize={12} fontFamily="Roboto" style={{color:'#025c'}} m={2}>
        Estado : {props.estado}
      </Box>
      <Box fontWeight={500} textAlign='left' fontSize={12} fontFamily="Roboto" style={{color:'#025c'}} m={2}>
        Categoria : {props.categoria}
      </Box>
      <Box fontWeight={500} textAlign='left' fontSize={12} fontFamily="Roboto" style={{color:'#025c'}} m={2}>
        Setor : {props.setor}
      </Box>
      </Typography>
          </DialogContentText>
      <form style={{marginTop:10}} noValidate>

        <TextField
        id="time"
        label="Tempo Mínimo"
        type="time"
        defaultValue={props.tempomínimo}
        disabled
        style={{width:200}}
        InputLabelProps={{
          shrink: true,
        }}
        inputProps={{
          step: 300, // 5 min
        }}
      />
      <TextField
        id="time"
        label="Tempo Máximo"
        type="time"
        disabled
        style={{width:200}}
        InputLabelProps={{
          shrink: true,
        }}
        inputProps={{
          step: 300, // 5 min
        }}
      />

      <div style={{display:'flex'}}>
      <TextField
        id="datetime-local"
        label="Data de Empréstimo"
        type="datetime-local"
        margin = 'normal'
        value={values.dataemprestimo}
        onChange={handleChange('dataemprestimo')}
        InputLabelProps={{
          shrink: true,
        }}
        helperText="Considere o Tempo Mínimo"
      />
      <TextField
        id="datetime-local"
        label="Data de Devolução"
        type="datetime-local"
        margin = 'normal'
        value={values.datadevolucao}
        onChange={handleChange('datadevolucao')}
        InputLabelProps={{
          shrink: true,
        }}
        helperText="Considere o Tempo Máximo"
      />
            </div>

    </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseTo} color="primary" size='large' variant='outlined'>
            Reservar
          </Button>

        </DialogActions>
        <ListItem button onClick={handleClick} style={{backgroundColor:'rgba(156, 159, 156,0.4)', elevation:3}}>
        <ListItemText primary=" Histórico do Recurso " align='center' variant='outlined'/>
        {value? <ExpandLess /> : <ExpandMore />}
      </ListItem>
      <Collapse in={value} timeout="auto" unmountOnExit>
      <Paper style={{ overflowX: 'auto'}}>
      <Table >
        <TableHead>
          <TableRow>
            <TableCell>Data de Início de Alocação</TableCell>
            <TableCell align="right">Autorizador</TableCell>
            <TableCell align="right">Solicitador</TableCell>
            <TableCell align="right">Data de Término de Alocação</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.calories}</TableCell>
              <TableCell align="right">{row.fat}</TableCell>
              <TableCell align="right">{row.carbs}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Paper>
      </Collapse>
      </Dialog>
      <CardActionArea onClick={handleClickOpen}>
      <CardMedia
          component="img"
          alt="Contemplative Reptile"
          height="140"
          image={require('./download.jpeg')}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
          {props.title}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {props.descricao}
          </Typography>
        </CardContent>

      <CardActions>
        <Fab variant="extended" aria-label="Delete" style={{marginRight:0,width:330}}>
        <NavigationIcon style={{marginRight:0}} />
        Botão
      </Fab>
      </CardActions>
      </CardActionArea>
    </Card>)
    };
